    // eslint-disable-next-line cypress/no-unnecessary-waiting
    cy.wait(1000);
    cy.get("[data-test-countdown]").then(el => {
      const instantTwo = el.text();
      expect(instantOne).to.match(/\ds$/);
      // eslint-disable-next-line cypress/no-unnecessary-waiting
      cy.wait(1000);
      expect(instantTwo).not.eq(instantOne);
